import React, { useState } from 'react';
import './ExpenseForm.css';
const ExpenseForm = () => {

    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: ''
    });

    const titleChangeHandler = (event) => {
        setUserInput((prevState) => {
            return { ...prevState, enteredTitle: event.target.value };
        });
    };

    const amountChangeHandler = (event) => {
        setUserInput((prevState) =>{
            return{ ...prevState, enteredAmount: event.target.vallue};
        });
    };

    const dateChangeHandler = (event) => {
        setUserInput((prevState) => {
        return{ ...prevState, enteredDate: event.target.vallue};
         });
    };

    const submitHandler = (event) => {
        event.preventDefault();


        console.log(userInput);
    };

    return (
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={amountChangeHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2024-12-31' onChange={dateChangeHandler}  />
                </div>
                <div className='new-expense__actions'>
                    <button type='submit' onChange={submitHandler}>Add Expense</button>
                </div>
            </div>
        </form>
    );
};

export default ExpenseForm;
